import React, { useState } from 'react';
import axios from 'axios';
import { useSpring, animated } from 'react-spring';

function App() {
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [response, setResponse] = useState('');

  // Animation hooks for form and response message
  const formAnimation = useSpring({
    opacity: 1,
    transform: 'translateY(0)',
    from: { opacity: 0, transform: 'translateY(-30px)' },
  });

  const responseAnimation = useSpring({
    opacity: response ? 1 : 0,
    transform: response ? 'translateY(0)' : 'translateY(30px)',
    from: { opacity: 0, transform: 'translateY(30px)' },
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = { name, phone };

    try {
      const res = await axios.post('http://localhost:5000/call', data);
      setResponse(res.data.message);
    } catch (error) {
      setResponse('Error making the call');
    }
  };

  return (
    <div className="relative min-h-screen bg-gray-100">
      {/* Background Video */}
      <video
        className="absolute top-0 left-0 w-full h-full object-cover"
        autoPlay
        loop
        muted
      >
        <source src="https://www.w3schools.com/html/mov_bbb.mp4" type="video/mp4" />
      </video>

      {/* Overlay for content */}
      <div className="absolute top-0 left-0 w-full h-full bg-black opacity-50"></div>

      {/* Main Content */}
      <div className="relative z-10 min-h-screen flex flex-col items-center justify-center text-white">
        {/* Header */}
        <header className="text-center mb-12">
          <h1 className="text-5xl font-bold">VoiceOps</h1>
          <p className="text-xl mt-2">Voice-to-Voice Agents for Loans</p>
        </header>

        {/* Animated Form */}
        <animated.div style={formAnimation} className="bg-white p-8 rounded-lg shadow-lg max-w-md w-full">
          <h2 className="text-2xl font-bold text-center mb-6">Request a Call</h2>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-sm font-semibold mb-2">Name</label>
              <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
                className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
            <div>
              <label className="block text-sm font-semibold mb-2">Phone Number</label>
              <input
                type="text"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
                required
                className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
            <button
              type="submit"
              className="w-full bg-blue-600 text-white py-3 rounded-md hover:bg-blue-700 transition"
            >
              Make the Call
            </button>
          </form>

          {/* Response message */}
          <animated.p style={responseAnimation} className="mt-4 text-center text-lg font-semibold text-gray-800">
            {response}
          </animated.p>
        </animated.div>

        {/* Footer */}
        <footer className="w-full bg-black text-white text-center py-6 mt-12">
          <p>&copy; 2025 VoiceOps. All Rights Reserved.</p>
          <p>Contact Us: <a href="mailto:support@voiceops.com" className="underline">support@voiceops.com</a></p>
        </footer>
      </div>
    </div>
  );
}

export default App;
